 false, $resize = true);    $content = array('chat_id' => $telegram->ChatID(), 'reply_markup' => $keyb, 'text' => $text);    $telegram->sendMessage($content);}function sendAllTrainingCentresList(){    global $telegram, $trainingCentres;    $text = "Выберите учебные центры" . " 👇🏻";    $buttons = $callback_dates = $trainingCentres->getNames();    $option = array();    if (count($buttons) <= 5) {        for ($i = 0; $i < count($buttons); $i++) {            $option[] = array($telegram->buildInlineKeyBoardButton("☑️" . $buttons[$i] . "☑️", $url = '', $callback_data = $callback_dates[$i]));        }    } else {        for ($i = 0; $i < 5; $i++) {            $option[] = array($telegram->buildInlineKeyBoardButton("☑️" . $buttons[$i] . "☑️", $url = '', $callback_data = $callback_dates[$i]));            $option[] = array($telegram->buildInlineKeyBoardButton("⬅️", $url = '', $callback_data = 'back'), $telegram->buildInlineKeyBoardButton("➡️", $url = '', $callback_data = 'forward'));        }    }    $keyb = $telegram->buildInlineKeyBoard($option);    $content = array('chat_id' => $telegram->ChatID(), 'reply_markup' => $keyb, 'text' => $text);    $telegram->sendMessage($content);}function sendTrainingCentresList($buttons){    global $telegram;    if ($buttons !== []) {        $text = "Выберите учебные центры" . " 👇🏻";        $callback_dates = $buttons;        $option = array();        if (count($buttons) <= 5) {            for ($i = 0; $i < count($buttons); $i++) {                $option[] = array($telegram->buildInlineKeyBoardButton("☑️" . $buttons[$i] . "☑️", $url = '', $callback_data = $callback_dates[$i]));            }        } else {            for ($i = 0; $i < 5; $i++) {                $option[] = array($telegram->buildInlineKeyBoardButton("☑️" . $buttons[$i] . "☑️", $url = '', $callback_data = $callback_dates[$i]));                $option[] = array($telegram->buildInlineKeyBoardButton("⬅️", $url = '', $callback_data = 'back'), $telegram->buildInlineKeyBoardButton("➡️", $url = '', $callback_data = 'forward'));            }        }        $keyb = $telegram->buildInlineKeyBoard($option);        $content = array('chat_id' => $telegram->ChatID(), 'reply_markup' => $keyb, 'text' => $text);        $telegram->sendMessage($content);    } else {        $content = array('chat_id' => $telegram->ChatID(), 'text' => "Нет учебных центров в этом тумане 😔");        $telegram->sendMessage($content);    }}function sendChangeLangInlineKeyboard(){    global $telegram;    $option = [        [            $telegram->buildInlineKeyBoardButton('🇷🇺 Русский язык', $url = '', $callback_data = 'ru'),            $telegram->buildInlineKeyBoardButton('🇺🇿 O\'zbek tili', $url = '', $callback_data = 'uz'),        ],    ];    $keyb = $telegram->buildInlineKeyBoard($option);    $content = ['chat_id' => $telegram->ChatID(), 'reply_markup' => $keyb, 'text' => "Выберите язык:"];    $telegram->sendMessage($content);}function sendDistricts(){    global $districts, $user;    $mdistricts = $districts->getAllDistricts();    for ($i = 0; $i < count($mdistricts); $i++) {        $mdistricts[$i] = "📍 " . $mdistricts[$i];    }    msendKeyBoard($mdistricts, $user->getText('text_choose_district')." 👇🏻");}